(()=>{const e=document.getElementById("submitcityname"),t=document.getElementById("cityname");e.addEventListener("click",(e=>{e.preventDefault(),console.log("in process"),async function(){const e=`https://api.weatherapi.com/v1/current.json?key=5f3744c6037d48dd9f2161349241204&q=${t.value}`;try{const t=await fetch(e),n=await t.json();console.log(n),document.getElementById("weathercardContainer").innerHTML=`<ul>Temperature:${n.current.feelslike_c}C</ul>\n                              <ul>Wind:${n.current.wind_kph}  kph</ul>\n                              <ul>Humidity:${n.current.humidity}</ul>  `}catch(e){return console.log("Error Fetching API data",e),null}}()}))})();